#!/bin/bash
set -euo pipefail

HOST="aws-0-sa-east-1.pooler.supabase.com"
PORT="5432"

echo "🔍 Extraindo certificado raiz da Supabase..."
echo ""

# Extrair todos os certificados da cadeia
FULL_OUTPUT=$(echo | openssl s_client -connect "${HOST}:${PORT}" \
  -servername "${HOST}" -starttls postgres 2>&1)

# Extrair apenas os blocos de certificado
CERTS=$(echo "$FULL_OUTPUT" | awk '/BEGIN CERTIFICATE/,/END CERTIFICATE/')

# Contar quantos certificados existem
CERT_COUNT=$(echo "$CERTS" | grep -c "BEGIN CERTIFICATE" || echo "0")

if [ "$CERT_COUNT" -eq "0" ]; then
  echo "❌ Erro: Nenhum certificado encontrado"
  exit 1
fi

echo "✅ Encontrados $CERT_COUNT certificado(s) na cadeia"
echo ""

# Extrair o último certificado (raiz) - mais simples
ROOT_CERT=$(echo "$CERTS" | awk '
  /BEGIN CERTIFICATE/ { 
    cert=""
    flag=1
  }
  flag {
    cert=cert $0 "\n"
  }
  /END CERTIFICATE/ {
    if (flag) {
      last_cert=cert
      flag=0
    }
  }
  END {
    print last_cert
  }
')

# Verificar se é autoassinado
ISSUER=$(echo "$ROOT_CERT" | openssl x509 -noout -issuer 2>/dev/null | sed 's/issuer=//')
SUBJECT=$(echo "$ROOT_CERT" | openssl x509 -noout -subject 2>/dev/null | sed 's/subject=//')

echo "📋 Informações do Certificado Raiz:"
echo "   Subject: $SUBJECT"
echo "   Issuer:  $ISSUER"
echo ""

if [ "$ISSUER" = "$SUBJECT" ]; then
  echo "⚠️ Certificado raiz é AUTOASSINADO (issuer == subject)"
else
  echo "✅ Certificado raiz não é autoassinado"
fi

